import { cn } from "@/lib/cn";
import { type VariantProps } from "native-variants";
import { styled } from "@/lib/nva";
import { View, Text } from "react-native";
import { type ComponentProps } from "react";

type CommonProps = {
  min: number;
  value: number;
  max: number;
};

const progressVariants = styled((ctx, t) =>
  ctx({
    slots: ["root", "wrapper", "bar", "label"],
    base: {
      root: {
        width: "100%",
        display: "flex",
        overflow: "hidden",
        gap: t.spacing["1"],
        flexDirection: "column",
      },
      wrapper: {
        borderRadius: t.radii.full,
        backgroundColor: t.colors.input,
      },
      bar: {
        height: "100%",
        borderRadius: t.radii.full,
        backgroundColor: t.colors.primary,
      },
      label: {
        textAlign: "right",
        fontSize: t.fontSizes.xxs,
        color: t.colors.mutedForeground,
      },
    },
    variants: {
      size: {
        default: {
          wrapper: {
            height: 8,
          },
        },
        sm: {
          wrapper: {
            height: 6,
          },
        },
        lg: {
          wrapper: {
            height: 10,
          },
        },
      },
    },
    defaultVariants: {
      size: "default",
    },
  })
);

function getProgressValue(config: CommonProps): number {
  const { max, min, value } = config;

  const clampedValue = Math.min(Math.max(value, min), max);
  return max - min === 0 ? 0 : (clampedValue - min) / (max - min);
}

function Progress({
  min = 0,
  value = 0,
  max = 100,
  size,
  ...props
}: ComponentProps<typeof View> &
  CommonProps &
  VariantProps<typeof progressVariants>) {
  const styles = progressVariants({ size });
  const progress = getProgressValue({ min, value, max });

  return (
    <View style={styles.root} {...props}>
      <View style={styles.wrapper}>
        <View style={cn(styles.bar, { width: `${progress * 100}%` })} />
      </View>
      <Text style={styles.label}>{`${value} / ${max}`}</Text>
    </View>
  );
}

export { Progress };

import { StyleProp } from "react-native";

function flattenStyle<T>(style: StyleProp<T>): T[] {
  if (!style) return [];
  if (Array.isArray(style)) {
    return style.flat
      ? (style.flat().filter(Boolean) as T[])
      : (style.filter(Boolean) as T[]);
  }
  // @ts-ignore
  return [style];
}

export function cn<T extends object>(...styles: StyleProp<T>[]): T {
  return styles
    .flatMap(flattenStyle)
    .filter((s): s is T => !!s)
    .reduce<T>((acc, style) => ({ ...acc, ...style }), {} as T);
}
