import { cn } from "@/lib/cn";
import { type VariantProps } from "native-variants";
import { styled } from "@/lib/nva";
import { type ComponentProps } from "react";
import { View } from "react-native";

const separatorVariants = styled((ctx, t) =>
  ctx({
    slots: ["root"],
    base: {
      root: {
        backgroundColor: t.colors.border,
      },
    },
    variants: {
      orientation: {
        vertical: {
          root: {
            height: "100%",
            width: 0.5,
          },
        },
        horizontal: {
          root: {
            width: "100%",
            height: 0.5,
          },
        },
      },
    },
    defaultVariants: {
      orientation: "horizontal",
    },
  })
);

function Separator({
  style,
  orientation,
  ...props
}: ComponentProps<typeof View> & VariantProps<typeof separatorVariants>) {
  const styles = separatorVariants({ orientation });

  return <View style={cn(styles.root, style)} {...props} />;
}

export { Separator };

import { StyleProp } from "react-native";

function flattenStyle<T>(style: StyleProp<T>): T[] {
  if (!style) return [];
  if (Array.isArray(style)) {
    return style.flat
      ? (style.flat().filter(Boolean) as T[])
      : (style.filter(Boolean) as T[]);
  }
  // @ts-ignore
  return [style];
}

export function cn<T extends object>(...styles: StyleProp<T>[]): T {
  return styles
    .flatMap(flattenStyle)
    .filter((s): s is T => !!s)
    .reduce<T>((acc, style) => ({ ...acc, ...style }), {} as T);
}
